listeners.beforesubmit:
function(){      
                           var practiceName = this.find('name', './practiceName')[0];
                           var practiceHeading = this.find('name', './practiceHeading')[0];
                           if(practiceName.getValue().length < 0){
                                 CQ.Ext.Msg.show(
									{title: 'practiceName',  msg: 'practiceName should not be empty ',icon:CQ.Ext.MessageBox.WARNING,buttons: CQ.Ext.Msg.OK}
                                                                   );return false;
                           }

						    if((practiceHeading.getValue().length < 0 )){
                                 CQ.Ext.Msg.show(
									{title: 'PracticeHeading',  msg: 'Practice Heading must not be empty',icon:CQ.Ext.MessageBox.WARNING,buttons: CQ.Ext.Msg.OK}
                                                                   );return false;
                           }

                           }
